import { useMemo, useState } from "react";
import { IShoppingSaleUnit } from "../api/structures/shoppings/sales/IShoppingSaleUnit";
import OptionSelect from "./OptionSelect";

interface Props {
  unit: IShoppingSaleUnit;
}

export default function ProductSection({ unit }: Props) {
  const { options, name } = unit;
  const [optionkeys, setOptionKeys] = useState<string[]>([]);
  const stockKey = useMemo(() => {
    console.log("optionkeys:", optionkeys);
    return optionkeys.join(", ");
  }, [optionkeys]);

  const onSelectOption = (optionIndex: number, candidateIndex: number) => {
    setOptionKeys((prev) => {
      const result = prev;
      const selectedOption = options[optionIndex];
      const selectedCandidates = selectedOption.candidates[candidateIndex];
      result[optionIndex] = selectedCandidates.name;
      return [...result];
    });
  };

  console.log("unit:", unit);
  console.log("optionKeys:", stockKey);

  return (
    <section>
      <header>
        <h6>{name}</h6>
      </header>
      <main>
        <ul>
          {options.map((option, index) => (
            <li key={option.id}>
              <OptionSelect
                optionName={option.name}
                optionIndex={index}
                candidates={option.candidates}
                onSelectOption={onSelectOption}
              />
            </li>
          ))}
        </ul>
      </main>
    </section>
  );
}
