import { IShoppingSaleUnitOptionCandidate } from "../api/structures/shoppings/sales/IShoppingSaleUnitOptionCandidate";

type Props = {
  optionName: string;
  optionIndex: number;
  candidates: IShoppingSaleUnitOptionCandidate[];
  onSelectOption: (optionIndex: string, candidateIndex: number) => void;
};

export default function OptionSelect({
  optionName,
  optionIndex,
  candidates,
  onSelectOption,
}: Props) {
  const handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const {
      target: { value },
    } = e;
    const index = +value;
    onSelectOption(optionIndex, index);
  };

  return (
    <select onChange={handleChange}>
      <option value="">--Please choose an option--</option>
      {candidates.map((candidate, index) => (
        <option key={candidate.id} value={index}>
          {`${optionName} / ${candidate.name}`}
        </option>
      ))}
    </select>
  );
}
