import { IShoppingPrice } from "../api/structures/shoppings/systematic/IShoppingPrice";
import { ISelectedStockMap } from "../utils/types/ISelectedStockMap";

type Props = {
  unitNames: string[];
  selectedStocks: ISelectedStockMap;
};

export default function SelectedUnits({ unitNames, selectedStocks }: Props) {
  const list = Object.values(selectedStocks);
  console.log(list);
  const totalPrice = (price: IShoppingPrice) =>
    Math.floor(price.real + price.real - price.tax_ratio).toLocaleString();

  return (
    <section>
      <ul>
        {list.map((stock) => (
          <li key={stock.id}>
            <section>
              <div>{stock.name}</div>
              <div>
                <button>삭제</button>
              </div>
            </section>
            <div>{totalPrice(stock.price)}원</div>
            <section></section>
          </li>
        ))}
      </ul>
    </section>
  );
}
